len = length(data);

% Direct
byte = [];
for i = 4:4:len
    num = 8*data(i-3)+4*data(i-2)+2*data(i-1)+data(i);
    byte = [byte dec2hex(num)];
end

% Inverted
dataInv = -1.*data+1;
inverted = [];
for i = 4:4:len
    num = 8*dataInv(i-3)+4*dataInv(i-2)+2*dataInv(i-1)+dataInv(i);
    inverted = [inverted dec2hex(num)];
end

% Shift 1
dataShiftOne = data(2:end);
lenShiftOne = length(dataShiftOne);
shiftOne = [];
for i = 4:4:lenShiftOne
    num = 8*dataShiftOne(i-3)+4*dataShiftOne(i-2)+2*dataShiftOne(i-1)+dataShiftOne(i);
    shiftOne = [shiftOne dec2hex(num)];
end

% Shift 1 inverted
dataInvShiftOne = dataInv(2:end);
invertedShiftOne = [];
for i = 4:4:lenShiftOne
    num = 8*dataInvShiftOne(i-3)+4*dataInvShiftOne(i-2)+2*dataInvShiftOne(i-1)+dataInvShiftOne(i);
    invertedShiftOne = [invertedShiftOne dec2hex(num)];
end

